using Store_API.Models;
using Microsoft.EntityFrameworkCore;

namespace Store_API.Migrations;

public class ProductDbContext : DbContext
{
    public DbSet<Product> Product { get; set; }
    public ProductDbContext(DbContextOptions<ProductDbContext> options)
        : base(options)
    {
    }


    protected override void OnModelCreating(ModelBuilder modelBuilder)
    {
        base.OnModelCreating(modelBuilder);

        modelBuilder.Entity<Product>(entity =>
        {
            entity.HasKey(e => e.ProductId);
            entity.Property(e => e.Title).IsRequired();
            entity.Property(e => e.Price).IsRequired();
            entity.Property(e => e.Description).IsRequired();
            entity.Property(e => e.Picture).IsRequired();
        });


    modelBuilder.Entity<Product>().HasData
    (
        new Product 
        { 
            ProductId = 1,
            Title = "Ocellaris Clownfish",
            Price = 24.99,
            Description = "The Ocellaris Clownfish may be one of the aquarium industry's most popular marine fish. Its beautiful orange body dressed with white bands outlined in black instantly distinguishes the Ocellaris Clownfish. The Ocellaris Misbar have broken bars or bars that do not connect around the entire body. The Extreme Misbars have very few bars and could be only on one side of the fish's body. This member of the Pomacentridae family is an excellent addition to almost any saltwater aquarium system. What makes this specific variety even more appealing to aquarists is the fact that each Ocellaris Clownfish is captive-bred to help protect the fragile reef environments of the world.",
            Picture = "data:image/jpeg;base64,"
        },
        new Product 
        { 
            ProductId = 2,
            Title = "Green Mandarin",
            Price = 29.99,
            Description = "The head and body of the Green Mandarin, also known as the Striped Mandarinfish or Green Mandarinfish, are a mazelike combination of blue, orange, and green. Males may be distinguished from females by their more-elongated first dorsal spine. It is extremely popular with aquarists because of its unusual beauty.It should be kept in a well-established 30 gallon or larger aquarium with live rock, live sand, and plenty of hiding places. It is moderately hardy in reef aquariums if given special care. It is not overly aggressive towards other fish, except for conspecifics.",
            Picture = "http://cdn.shopify.com/s/files/1/1533/6325/products/mandarin_goby2_1024x1024.jpg?v=1582472060"
        }
    );
    }
}